package main

import "github.com/goforj/collection"

func main() {
	// Chunk splits the collection into chunks of the given size.
	// The final chunk may be smaller if len(items) is not divisible by size.

	// Example: integers
	c := collection.New([]int{1, 2, 3, 4, 5}).Chunk(2)
	collection.Dump(c)

	// #[][]int [
	//  0 => #[]int [
	//    0 => 1 #int
	//    1 => 2 #int
	//  ]
	//  1 => #[]int [
	//    0 => 3 #int
	//    1 => 4 #int
	//  ]
	//  2 => #[]int [
	//    0 => 5 #int
	//  ]
	//]

	// Example: structs
	type User struct {
		ID   int
		Name string
	}

	users := []User{
		{ID: 1, Name: "Alice"},
		{ID: 2, Name: "Bob"},
		{ID: 3, Name: "Carol"},
		{ID: 4, Name: "Dave"},
	}

	userChunks := collection.New(users).Chunk(2)
	collection.Dump(userChunks)

	// Dump output will show [][]User grouped in size-2 chunks, e.g.:
	// #[][]main.User [
	//  0 => #[]main.User [
	//    0 => #main.User {
	//      +ID   => 1 #int
	//      +Name => "Alice" #string
	//    }
	//    1 => #main.User {
	//      +ID   => 2 #int
	//      +Name => "Bob" #string
	//    }
	//  ]
	//  1 => #[]main.User [
	//    0 => #main.User {
	//      +ID   => 3 #int
	//      +Name => "Carol" #string
	//    }
	//    1 => #main.User {
	//      +ID   => 4 #int
	//      +Name => "Dave" #string
	//    }
	//  ]
	//]
}

clone.code:
//go:build ignore
// +build ignore

package main

import "github.com/goforj/collection"

func main() {
	// Clone returns a copy of the collection.
	// @chainable true
	// @terminal false
	// 
	// The returned collection has its own backing slice, so subsequent mutations
	// do not affect the original collection.
	// 
	// Clone is intended to be used when branching a pipeline while preserving
	// the original collection.

	// Example: basic cloning
	c := collection.New([]int{1, 2, 3})
	clone := c.Clone()

	clone = clone.Append(4)

	collection.Dump(c.Items())
	// #[]int [
	//   0 => 1 #int
	//   1 => 2 #int
	//   2 => 3 #int
	// ]

	collection.Dump(clone.Items())
	// #[]int [
	//   0 => 1 #int
	//   1 => 2 #int
	//   2 => 3 #int
	//   3 => 4 #int
	// ]

	// Example: branching pipelines
	base := collection.New([]int{1, 2, 3, 4, 5})

	evens := base.Clone().Filter(func(v int) bool {
		return v%2 == 0
	})

	odds := base.Clone().Filter(func(v int) bool {
		return v%2 != 0
	})

	collection.Dump(base.Items())
	// #[]int [
	//   0 => 1 #int
	//   1 => 2 #int
	//   2 => 3 #int
	//   3 => 4 #int
	//   4 => 5 #int
	// ]

	collection.Dump(evens.Items())
	// #[]int [
	//   0 => 2 #int
	//   1 => 4 #int
	// ]

	collection.Dump(odds.Items())
	// #[]int [
	//   0 => 1 #int
	//   1 => 3 #int
	//   2 => 5 #int
	// ]
}

