	//   1 => 20 #int
	//   2 => 30 #int
	// ]

	// Example: strings
	c2 := collection.New([]string{"apple", "banana", "cherry"})

	upper := c2.Map(func(s string) string {
		return strings.ToUpper(s)
	})

	collection.Dump(upper.Items())
	// #[]string [
	//   0 => "APPLE" #string
	//   1 => "BANANA" #string
	//   2 => "CHERRY" #string
	// ]

	// Example: structs
	type User struct {
		ID   int
		Name string
	}

	users := collection.New([]User{
		{ID: 1, Name: "Alice"},
		{ID: 2, Name: "Bob"},
	})

