	// Example: take none
	out2 := c.TakeLast(0)
	collection.Dump(out2.Items())
	// #[]int [
	// ]

	// Example: take all
	out3 := c.TakeLast(10)
	collection.Dump(out3.Items())
	// #[]int [
	//   0 => 1 #int
	//   1 => 2 #int
	//   2 => 3 #int
	//   3 => 4 #int
	//   4 => 5 #int
	// ]

	// Example: structs
	type User struct {
		ID int
	}

	users := collection.New([]User{
		{ID: 1},
		{ID: 2},
		{ID: 3},
	})

	out4 := users.TakeLast(1)
	collection.Dump(out4.Items())
	// #[]main.User [
	//  0 => #main.User {
	//    +ID => 3 #int
	//  }
	// ]
}

