	// true #bool

	// Example: strings
	c2 := collection.New([]string{"alpha", "beta", "gamma"})

	v2, ok2 := c2.First()
	collection.Dump(v2, ok2)
	// "alpha" #string
	// true #bool

	// Example: structs
	type User struct {
		ID   int
		Name string
	}

	users := collection.New([]User{
		{ID: 1, Name: "Alice"},
		{ID: 2, Name: "Bob"},
	})

	u, ok3 := users.First()
	collection.Dump(u, ok3)
	// #main.User {
	//   +ID   => 1 #int
	//   +Name => "Alice" #string
	// }
	// true #bool

	// Example: integers - empty collection
	c3 := collection.New([]int{})
	v3, ok4 := c3.First()
	collection.Dump(v3, ok4)
	// 0 #int
	// false #bool
}

