	strs := collection.New([]string{"a", "b"})
	more := collection.New([]string{"c", "d"})

	merged2 := strs.Merge(more)
	collection.Dump(merged2.Items())
	// #[]string [
	//   0 => "a" #string
	//   1 => "b" #string
	//   2 => "c" #string
	//   3 => "d" #string
