package main

import "github.com/goforj/collection"

func main() {
	// First returns the first element in the collection.
	// If the collection is empty, ok will be false.

	// Example: integers
	c := collection.New([]int{10, 20, 30})

	v, ok := c.First()
	collection.Dump(v, ok)
