package main

import "github.com/goforj/collection"

func main() {
// ToMap reduces a collection into a map using the provided key and value
// selector functions.

// Example: basic usage
users := []string{"alice", "bob", "carol"}

out := collection.ToMap(
collection.New(users),
func(name string) string { return name },
func(name string) int { return len(name) },
)

collection.Dump(out)
// #map[string]int {
//  alice => 5 #int
//  bob => 3 #int
//  carol => 5 #int
// }

// Example: re-keying structs
type User struct {
ID   int
Name string
}

users2 := []User{
{ID: 1, Name: "Alice"},
{ID: 2, Name: "Bob"},
}

byID := collection.ToMap(
collection.New(users2),
func(u User) int { return u.ID },
func(u User) User { return u },
)

collection.Dump(byID)
// #map[int]main.User {
//  1 => #main.User {
//    +ID   => 1 #int
//    +Name => "Alice" #string
//  }
//  2 => #main.User {
//    +ID   => 2 #int
//    +Name => "Bob" #string
//  }
// }
}

